def _get_value_from_facts(self, variable_name, distribution, default_value):
    attr = getattr(self, variable_name)
    value = attr.get(
        distribution['name'] + distribution['version'],
        attr.get(
            distribution['name'],
            attr.get(
                distribution['family'],
                getattr(self, default_value)))
    )
    return value

