    """If the cwd is writable, warn and skip it """
    real_stat = os.stat

    def _os_stat(path):
        if path == working_dir:
            from posix import stat_result
            stat_info = list(real_stat(path))
            stat_info[stat.ST_MODE] |= stat.S_IWOTH
            return stat_result(stat_info)
        else:
            return real_stat(path)

